package introtojava.Day2;

public class ArithmeticOperations {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int number1;
		int number2;
		
		number1 = 3;
		number2 = 6;
		
		System.out.println(number2 / number1);
		System.out.println(number2 - number1);
		System.out.println(number2 * number1);
		System.out.println(number2 + number1);
	}

}
